import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js';

const RadarChart = () => {
  const chartRef = useRef(null);

  useEffect(() => {
    const data = {
      labels: ['Python', 'Javascript', 'Java', 'HTML', 'CSS'],
      datasets: [
        {
          label: 'Skill level',
          data: [5, 6, 3, 2, 4], // Example data values
          backgroundColor: [
            'rgba(5, 22, 192, 0.2)', // Color for data point 1
            'rgba(22, 12, 182, 0.2)', // Color for data point 2
            'rgba(75, 193, 172, 0.2)', // Color for data point 3
            'rgba(75, 142, 162, 0.2)', // Color for data point 4
            'rgba(75, 133, 152, 0.2)', // Color for data point 5
          ],
          borderColor: 'rgba(75, 192, 192, 1)', // Example border color
          borderWidth: 1,
        },
      ],
    };

    const options = {
      scale: {
        pointLabels: {
          fontSize: 16,
        },
        ticks: {
          display: false,
          beginAtZero: true,
        },
      },
    };

    const radarChart = new Chart(chartRef.current, {
      type: 'radar',
      data: data,
      options: options,
    });

    return () => {
      radarChart.destroy();
    };
  }, []);

  return <canvas ref={chartRef} />;
};

export default RadarChart;
