import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js';

const RadarChart = () => {
  const chartRef = useRef(null);

  useEffect(() => {
    // Register custom plugin to draw hexagon shapes at each tick
    Chart.pluginService.register({
      afterDraw: function (chart) {
        const ctx = chart.ctx;
        const xAxis = chart.scales['x-axis-0'];

        // Calculate radius based on the maximum value in the dataset
        const maxDataValue = Math.max(...chart.data.datasets[0].data);
        const radius = (Math.min(chartRef.current.offsetWidth, chartRef.current.offsetHeight) / 2) * (maxDataValue / chart.options.scales.y.ticks.max);

        // Calculate angle increment
        const angleIncrement = (2 * Math.PI) / xAxis.ticks.length;

        // Draw hexagon shapes at each tick
        xAxis.ticks.forEach((label, index) => {
          const angle = index * angleIncrement;

          const x = chartRef.current.offsetWidth / 2 + Math.cos(angle) * radius;
          const y = chartRef.current.offsetHeight / 2 + Math.sin(angle) * radius;

          ctx.beginPath();
          ctx.moveTo(x + radius * Math.cos(0), y + radius * Math.sin(0));

          for (let side = 1; side < 6; side++) {
            const sideAngle = (2 * Math.PI * side) / 6;
            ctx.lineTo(
              x + radius * Math.cos(sideAngle),
              y + radius * Math.sin(sideAngle)
            );
          }

          ctx.closePath();
          ctx.lineWidth = 2;
          ctx.strokeStyle = 'rgba(0, 0, 0, 0.3)';
          ctx.stroke();
        });
      },
    });

    const data = {
      labels: ['Label 1', 'Label 2', 'Label 3', 'Label 4', 'Label 5'],
      datasets: [
        {
          data: [4, 3, 5, 2, 4], // Example data values
          backgroundColor: 'rgba(75, 192, 192, 0.4)', // Example background color
          borderColor: 'rgba(75, 192, 192, 1)', // Example border color
          borderWidth: 1,
        },
      ],
    };

    const options = {
      responsive: true,
      maintainAspectRatio: false,
      legend: {
        display: false,
      },
      scales: {
        y: {
          ticks: {
            display: false,
            beginAtZero: true,
            max: Math.ceil(Math.max(...data.datasets[0].data) / 10) * 10,
          },
        },
        r: {
          angleLines: {
            display: false,
          },
        },
      },
    };

    const radarChart = new Chart(chartRef.current, {
      type: 'radar',
      data: data,
      options: options,
    });

    return () => {
      radarChart.destroy();
    };
  }, []);

  return <canvas ref={chartRef} />;
};

export default RadarChart;
